import React, { useState, useEffect } from "react";
import "./SliderIMG.css";

const images = [
  "Cyber.jpg",
  "hackerlog.png",
  "Tech.jpg",
  "Web.jpg",
  "Lab.jpg",
  "Mec.jpg",
];

const ImageSlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 1500);

    return () => clearInterval(intervalId);
  }, []);

  const getPrevIndex = (currentIndex) => {
    if (currentIndex === 0) {
      return images.length - 1;
    } else {
      return currentIndex - 1;
    }
  };

  const getNextIndex = (currentIndex) => {
    if (currentIndex === images.length - 1) {
      return 0;
    } else {
      return currentIndex + 1;
    }
  };

  return (
    <div className="carousel-container container">
      <img
        src={`images/${images[getPrevIndex(currentIndex)]}`}
        alt={`Image ${getPrevIndex(currentIndex)}`}
        className="slide prev-slide"
      />
      <img
        src={`images/${images[currentIndex]}`}
        alt={`Image ${currentIndex}`}
        className="slide current-slide"
      />
      <img
        src={`images/${images[getNextIndex(currentIndex)]}`}
        alt={`Image ${getNextIndex(currentIndex)}`}
        className="slide next-slide"
      />
    </div>
  );
};

export default ImageSlider;
