import './App.css';
import { Header } from './sections/Header/Header';
import { Content } from './sections/Content/Content';
import { Footer } from './sections/Footer/Footer';
import ImageCarousel from './components/SlideIMG/ImageCarousel';
import Welcome from './components/Welcome/Welcome';
import SynthesiaVideoPlayer from './components/Video/Video';
import { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {

  const [numVisitors, setNumVisitors] = useState(0);

  useEffect(() => {
    axios.get('http://localhost:5000/visitors')
      .then(response => setNumVisitors(response.data.count))
      .catch(error => console.error(error));
  }, []);


  return (
    <div className='body'>
      <Header />
      <div className='Front'  >
          <Welcome/>  
        <div className="App container">
            <SynthesiaVideoPlayer/>
        b</div>
      </div>
      <ImageCarousel/>
      <Content />
      <div className="visitors-counter">
        <h1 id='vc-title'>Visitors Counter</h1>
        <h3>Number of visitors : {numVisitors} visitors</h3>
        <div className='spinners-container'>
          <div class="spinner-border text-primary" role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
          <div class="spinner-border text-secondary" role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
          <div class="spinner-border text-success" role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
          <div class="spinner-border text-danger" role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
          <div class="spinner-border text-warning" role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
          <div class="spinner-border text-info" role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
          <div class="spinner-border text-light" role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
          <div class="spinner-border text-dark" role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
        </div>  
      </div>
      <Footer />
    </div>
  );
}

export default App;
