import React from 'react'
import './Feedback.css'
import { Link } from "react-router-dom";
import Axios from "axios"
import { useState, useEffect } from 'react'

export const Feedback = () => {

    // useState to make API data extraction synchronous
  const [feedbacks, setfeedback] = useState([])

  useEffect(() => {
    // call the API by Axios to display feedbacks in the collection feedbacks in the PFA DB here
    Axios.get("http://localhost:5000/feedbacks")
    .then(res => {
      setfeedback(res.data)
    })
  }, [])

  return (
    <div className='container feedback-container'>
        <h3 className='feedback-title'>Some feedbacks shared by our students</h3>

        <div className='row m-1'>
      {feedbacks.slice(0,3).map(feedback => {
        return(
          <div className='col-3 container'>
          <div class="card bg-light" style={{width: "100%"}} key={feedback._id}>
          <img src="images/FEEDBACKICON" class="card-img-top" alt="feedback-image" />
            <div class="card-body">
              <h5 class="card-title">Formation Name : {feedback.formationName}</h5>
              <h6 class="card-text">Student First Name : {feedback.studentFirstName}</h6>
              <h6 class="card-text">Student Last Name : {feedback.studentLastName}</h6>
              <h6 class="card-text">Student Section : {feedback.studentSection}</h6>
              <h6 class="card-text">Feedback Content : {feedback.feedbackContent}</h6>
              <h6 class="card-text">Feedback Rating : {feedback.feedbackRating} Stars</h6>
            </div>
          </div>
          </div>
        )
      })}
      </div>
        <div className='btn btn-light mt-3'>
            <a><Link to="/feedback">Have a feedback to share with us ? click here</Link></a>
        </div>
    </div>
  )
}
