import React, { useState, useEffect } from 'react';
import './Inscri.css';

const InscriptionsFormationsTable = () => {
  const [inscriptions, setInscriptions] = useState([]);

  useEffect(() => {
    // Fetch data from the Express.js API
    fetch('http://localhost:5000/getEnrolledStudentsPerFormation')
      .then(response => response.json())
      .then(data => setInscriptions(data))
      .catch(error => console.error('Error fetching inscriptionsFormations:', error));
  }, []);

  return (
    <div className='inscription'>
      <h1>Inscriptions Formations</h1>
      <table border={1}>
        <thead>
          <tr>
            <th>Formation Name</th>
            <th>Formation ID</th>
            <th>Students</th>
          </tr>
        </thead>
        <tbody>
          {inscriptions.map(inscription => (
            <tr key={`${inscription.formationName}-${inscription.formationId}`}>
              <td>{inscription.formationName}</td>
              <td>{inscription.formationId}</td>
              <td>
                <ul>
                    <h3>Enrolled Students :</h3>
                  {inscription.students.map(student => (
                    <li key={`${student.studentFirstName}-${student.studentLastName}`}>
                      <p>Student First Name : {student.studentFirstName}</p>
                      <p>Student Last Name : {student.studentLastName}</p>
                      <p>Student Email: {student.studentEmail}</p>
                      <p>Student Code: {student.studentCode}</p>
                    </li>
                  ))}
                </ul>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default InscriptionsFormationsTable ;
